window.onload=function(){for(var d=document.getElementsByClassName("js-accordion"),a=0;a<d.length;a++)for(var e=d[a].getElementsByTagName("h2"),b=0;b<e.length;b++){var c=e[b];c.setAttribute("class","toggle");c.nextElementSibling.setAttribute("class","closed");c.onclick=function(){"toggle"==this.getAttribute("class")?(this.setAttribute("class","toggle-active"),this.nextElementSibling.setAttribute("class","")):(this.setAttribute("class","toggle"),this.nextElementSibling.setAttribute("class","closed"))}}};